import {useState, useEffect} from "react"
import './App.css';
import Board from "./components/Board.js"



function App() {

  const [chances, setChances] = useState(5) //initial number of chances
  const [word, setWord] = useState("")
  const [score, setScore] = useState(0)

  const fetchWord = async () => {
    const response = await fetch('http://localhost:5000/')
    const word = await response.json()
    setWord(word)

  }

  useEffect(() => {
    fetchWord();

  }, [])  


  return (
    <div className="main">
      <div className = "info">
        <h4>Score: {score}</h4>
      </div>
      <Board 
        word={word}
        setScore={setScore}
		    fetchWord={fetchWord}
      />
    </div>
  );
}

export default App;
