import './Board.css';
import { useEffect } from "react"

const row1 = "qwertyuiop";
const row2 = 'asdfghjkl';
const row3 = 'zxcvbnm';


export default function Board (){

    const word= "pizzaman";

    let letterList=[];

    [...word].forEach((char) => {
        letterList.push(<p className = "letter">{char}</p>)
    })


var keyRow1 = [];
var keyRow2 = [];
var keyRow3 = [];



function makeKeyBoardRow(row, keyRow){
    [...row].forEach((char)=> {
        keyRow.push(<p className="key">{char}</p>)
    })
}

makeKeyBoardRow(row1, keyRow1)
makeKeyBoardRow(row2, keyRow2)
makeKeyBoardRow(row3, keyRow3)








    return(
        <>
        <div className = "board">
            <div className = "word">
                {letterList}
            </div>
        </div>
         <div className = "keyboard">
            <div class = "keyRow">
                {keyRow1}
            </div>
            <div class = "keyRow">
                {keyRow2}
            </div>
            <div class = "keyRow">
                {keyRow3}
            </div>
        </div>
        </>
    )
}
