import { useState, useEffect } from "react"

export default function Timer (props) {

    const [timeRemaining, setTimeRemianing] = useState(60)

    useEffect(()=> {
        props.gameOver && setTimeRemianing(60)
        props.resetTimer && setTimeRemianing(60)
        props.setResetTimer(false)
        if(timeRemaining > 0){
            setTimeout(() => {
                setTimeRemianing(prevTimeRemaining => prevTimeRemaining - 1)
            }, 1000);
        } else {
            props.setGameOver(true)
        }
    }, [timeRemaining])

    return (
        <h4>Time remaining: {timeRemaining}</h4>
    )
}
