import { useRef, useEffect } from "react"
import "./HangDude.css" 

export default function HangDude(props) {

    const canvasRef = useRef(null)

    const drawHead = (ctx) => {
        let arcPosition = 0;
        const drawCircle = setInterval(() => {
            ctx.beginPath()
            ctx.arc(100, 50, 20, 0, arcPosition * Math.PI)
            ctx.stroke()
            arcPosition += .05
            if(arcPosition > 2.1){
                clearInterval(drawCircle)
            }
        }, 50)
      }

      const drawTorso = (ctx) => {
          let lineY = 70;
          const drawVerticalLine = setInterval(() => {
            ctx.beginPath()
            ctx.moveTo(100, lineY)
            ctx.lineTo(100, lineY + 10)
            ctx.stroke()
            lineY += 10;
            if(lineY > 130){
                clearInterval(drawVerticalLine)
            }
          }, 50)
      }

      const drawArm = (ctx, direction) => {
          let lineY = 90;
          let lineX = 100;
          const drawArmLine = setInterval(() => {
            ctx.beginPath()
            ctx.moveTo(lineX, lineY)
            if(direction === "left"){
                ctx.lineTo(lineX - 10, lineY - 10)
                ctx.stroke()
                lineX -= 10
                lineY -= 10
            } else {
                ctx.lineTo(lineX + 10, lineY - 10)
                ctx.stroke()
                lineX += 10
                lineY -= 10
            }

            if (lineX <= 50 || lineX >= 150){
                clearInterval(drawArmLine)
            }

          }, 100)
      }

      const drawLeg = (ctx, direction) => { 
        let lineY = 140;
        let lineX = 100;
        const drawLegLine = setInterval(() => {
          ctx.beginPath()
          ctx.moveTo(lineX, lineY)
          if(direction === "left"){
              ctx.lineTo(lineX - 10, lineY + 10)
              ctx.stroke()
              lineX -= 10
              lineY += 10
          } else {
              ctx.lineTo(lineX + 10, lineY + 10)
              ctx.stroke()
              lineX += 10
              lineY += 10
          }

          if (lineX <= 50 || lineX >= 150){
              clearInterval(drawLegLine)
          }

        }, 50)
      }

      useEffect(() => {

        const canvas = canvasRef.current
        const context = canvas.getContext('2d')
        context.lineWidth = 2;

        //Our draw come here
        switch(props.wrongGuesses){
            case 0:
                //clear the canvas
                break;
            case 1:
                drawHead(context)
                break;
            case 2:
                drawTorso(context)
                break;
            case 3: 
                drawArm(context, "left")  
                break;
            case 4:
                drawArm(context, "right")
                break;
            case 5: 
                drawLeg(context, "left")
                break;
            case 6:
                drawLeg(context, "right")
                break;
        }







      }, [drawHead])
    return(
        <div className = "hangDude">
            <canvas ref={canvasRef} {...props} width={200} height={200}/>
        </div>
    )
}
