import "./Leaderboard.css"
import { useEffect, useState } from "react"


export default function Leaderboard (props) {

    const [display, setDisplay] = useState(false)

    useEffect(()=> {
        props.displayLeaderboard === false ? setDisplay(true) : setDisplay(false)
    }, [props.displayLeaderboard])

    return (
        <>
        <div className="leaderboard" style={{display: display ? "block" : "none"}}> 
            <p>scores</p>
            <button onClick = {props.setDisplayLeaderboard(false)}>exit</button>
        </div> 

        <div>
            <button onClick = {props.setDisplayLeaderboard(true)}>leaderboard</button>  
        </div>
        </>
    )


}
