import { useState, useEffect } from "react"

export default function Timer () {

    const [timeRemaining, setTimeRemianing] = useState(0)

    useEffect(()=> {
        setTimeout(() => {
            setTimeRemianing(prevTimeRemaining => prevTimeRemaining - 1)
        }, 1000);

    }, [timeRemaining])

    return (
        <h4>Time remaining: {timeRemaining}</h4>
    )
}
