import {useState, useEffect} from "react"
import './App.css';
import Board from "./components/Board.js"
import Timer from "./components/Timer.js"



function App() {

  const [chances, setChances] = useState(5) //initial number of chances
  const [word, setWord] = useState("")
  const [score, setScore] = useState(0)
  const [gameOver, setGameOver] = useState(false)
  const [gameOverAlertDisplay, setGameOverAlertDisplay] = useState("none")
  const [resetTimer, setResetTimer ] = useState(false)


  const fetchWord = async () => {
    try {
      const response = await fetch('http://localhost:5000/')
      const word = await response.json()
      setWord(word)
      setResetTimer(true)
    } catch (err) {
      console.log(err + " Error fetching word from database.")
    }
  }

  useEffect(() => {fetchWord();}, [])  //initial word render

  useEffect(()=>{
    if(gameOver){
      //show giant game over thing
      setGameOverAlertDisplay("block")

      //then set score 0 
      setScore(0)
    }
    setGameOver(false)
  }, [gameOver])




  return (
    <div className="main">
      <div className = "info">
        <h4>Score: {score}</h4>
        {/* <h4>Time remaining:{timeRemaing} seconds</h4> */}
        <Timer
          gameOver={gameOver} 
          setGameOver={setGameOver}
          resetTimer={resetTimer}
          setResetTimer={setResetTimer}
        />
      </div>
      <Board 
        word={word}
        score={score}
        setScore={setScore}
		    fetchWord={fetchWord}
      />
      {/* <div className="gameOverAlert"style={{display: gameOverAlertDisplay}}> 
      <h3>Game Over!</h3>
      <h3>Score {score}</h3>
      <input placeholder = "username" type = "text"></input>
      <button >Add to leaderboard</button>
      <br></br>
      <button >Play Again</button>

     </div> */}
    </div>
  );
}

export default App;
