import './Board.css';

const row1 = "qwertyuiop";
const row2 = 'asdfghjkl';
const row3 = 'zxcvbnm';

let keyRow1 = [];
let keyRow2 = [];
let keyRow3 = [];



export default function Board (){

    const word= "pizzaman";



    let letterList=[];

    [...word].forEach((char) => {
        letterList.push(<p className = "letter">{char}</p>)
    })

    const makeKeyBoardRow = (row, keyRow) =>{
        [...row].forEach((char)=> {
            keyRow.push(<button className="key" onClick={alert("clicked")}>{char}</button>)
        })
    }

    makeKeyBoardRow(row1, keyRow1)
    makeKeyBoardRow(row2, keyRow2)
    makeKeyBoardRow(row3, keyRow3)

    return(
        <>
        <div className = "board">
            <div className = "word">
                {letterList}
            </div>
        </div>
         <div className = "keyboard">
            <div class = "keyRow">
                {keyRow1}
            </div>
            <div class = "keyRow">
                {keyRow2}
            </div>
            <div class = "keyRow">
                {keyRow3}
            </div>
        </div>
        </>
    )
}
