            {scores.forEach(score => {
                console.log(score.name)
            })}
            <button onClick = {toggleLeaderboard}>exit</button>
        </div> }

        <div>
            <button onClick = {toggleLeaderboard}>leaderboard</button>  
        </div>
        </>
    )


}
