import { useState, useEffect } from "react"

export default function Timer () {

    const [timeRemaining, setTimeRemianing] = useState(60)

    useEffect(()=> {
        resetTimer && setTimeRemianing(60)
        if(timeRemaining > 0){
            setTimeout(() => {
                setTimeRemianing(prevTimeRemaining => prevTimeRemaining - 1)
            }, 1000);
        }
    }, [timeRemaining])

    return (
        <h4>Time remaining: {timeRemaining}</h4>
    )
}
