import "./Leaderboard.css"
import { useState } from "react";


export default function Leaderboard () {

    const [displayLeaderboard, setDisplayLeaderboard] = useState(false)
    const [scores, setScores] = useState()



    const fetchScores = async () => {
        const response = await fetch('http://localhost:5000/getScores')
        const data = await response.json()
        const scoreItems = []
        data.forEach(score => {
            scoreItems.push(<li>{score.name}</li>)
        });
        setScores(scoreItems)
    }

    const toggleLeaderboard = () => {
        if(displayLeaderboard){
            setDisplayLeaderboard(false)
        } else {
            setDisplayLeaderboard(true)
            fetchScores()
        }
    }

    return (
        <>
       {displayLeaderboard && scores && <div className="leaderboard" > 
        <table>
            <tr>
                <th>Name</th>
                <th>Score</th>
            </tr>
        </table>

            <button onClick = {toggleLeaderboard}>exit</button>
        </div> }

        <div>
            <button onClick = {toggleLeaderboard}>leaderboard</button>  
        </div>
        </>
    )


}
