import './Board.css';
import { useEffect, useState } from "react"
import Key from "./Key.js"

const row1 = "qwertyuiop";
const row2 = 'asdfghjkl';
const row3 = 'zxcvbnm';


export default function Board (){

    const [correctGuesses, setCorrectGuesses] = useState([])

    const word= "pizzaman";
    let letterCount = 0;
    let letterList=[];
    var keyRow1 = [];
    var keyRow2 = [];
    var keyRow3 = [];

    [...word].forEach((char) => {
        letterList.push(<p key={letterCount} display="none" className = "letter">{char}</p>)
        letterCount ++;
    })

    const makeKeyBoardRow = (row, keyRow) => {
        [...row].forEach((char) => {
            keyRow.push(<Key word={word} correctGuesses={correctGuesses} setCorrectGuesses={setCorrectGuesses} key={char} char={char}/>)
        })
    }

    makeKeyBoardRow(row1, keyRow1)
    makeKeyBoardRow(row2, keyRow2)
    makeKeyBoardRow(row3, keyRow3)




    return(
        <>
        <div className = "board">
            <div className = "word">
                {letterList}
            </div>
        </div>
         <div className = "keyboard">
            <div className = "keyRow">
                {keyRow1}
            </div>
            <div className = "keyRow">
                {keyRow2}
            </div>
            <div className = "keyRow">
                {keyRow3}
            </div>
        </div>
        </>
    )
}
