import {useState, useEffect} from "react"
import './App.css';
import Board from "./components/Board.js"



function App() {

  const [chances, setChances] = useState(5) //initial number of chances
  const [word, setWord] = useState("")
  const [score, setScore] = useState(0)
  const [gameOver, setGameOver] = useState(false)

  const startTimer = () => {
    setTimeRemaining(60)
    setTimeout(() => {
      setTimeRemaining(prevTimeRemaing => prevTimeRemaing -  1)
    }, 1000)
  }

  const fetchWord = async () => {
    try {
      const response = await fetch('http://localhost:5000/')
      const word = await response.json()
      setWord(word)
      startTimer()
    } catch (err) {
      console.log(err + " Error fetching word from database.")
    }
  }

  useEffect(() => {
    fetchWord();

  }, [])  




  return (
    <div className="main">
      <div className = "info">
        <h4>Score: {score}</h4>
        {/* <h4>Time remaining:{timeRemaing} seconds</h4> */}
        <Timer
          setTimer={setTimer}
          setGameOver={setGameOver}  
        />
      </div>
      <Board 
        word={word}
        score={score}
        setScore={setScore}
		    fetchWord={fetchWord}
      />
    </div>
  );
}

export default App;
