import { useState, useEffect } from "react"

export default function Timer () {

    const [timeRemaining, setTimeRemianing] = useState(6)

    useEffect(()=> {
        if(timeRemaining > 0){
            const timer1 = setTimeout(() => {
                setTimeRemianing(prevTimeRemaining => prevTimeRemaining - 1)
            }, 1000);
        }
        clearTimeout(timer1)

    }, [timeRemaining])

    return (
        <h4>Time remaining: {timeRemaining}</h4>
    )
}
