import "./GameOver.css"
import { useState } from "react"


export default function GameOver ({ score, resetGame, gameOver, word}) {

  const [name, setName] = useState("")
  const [initGame, setInitGame] = useState(true)
  const [scoreSaved, setScoreSaved] = useState(false)
  const [saveError, setSaveError] = useState("")

  const postScore = async () => {

    if(name === "") {
      setSaveError("Please enter your name")
      return
    }

    const data = {
      name : name,
      score : score
    }

    const response = await fetch('http://localhost:5000/addScore', {
      method : 'POST',
      headers : {
        'Content-Type' : 'application/json'
      },
      body : JSON.stringify(data)
    })

    if(response.status === 200){
      setScoreSaved(true)
    } else {
      setSaveError("Server Error. Contact Developer")
    }
  }

  const handleChange = (event) => {
    setName(event.target.value)
  }

  const handleStartGame = () => {
    resetGame()
    setInitGame(false)
  }

  const handlePlayAgain = () => {
    resetGame()
    setScoreSaved(false)
    setSaveError("")
    setName("")
  }

  if(initGame){
    return (
      <>
        <div className = "gameInit">
          <h2>Welcome to WordMan 3000</h2>
          <p>Guess the correct word to up your score</p>
          <p>There are 350,000 possible words. Good Luck!</p>
          <button onClick={handleStartGame}>Start Game</button>
          <button>View Leaderboard</button>
        </div>
      </>
    )
  } else {
      return (
        <>
        <div className="gameOverAlert" style={{display: gameOver ? "block" : "none"}}> 
            <h3>Game Over!</h3>
            <h3>Score {score}</h3>
            <h4>The word was '{word}'</h4>
            {scoreSaved && <p>Score added to leaderboard!</p>}
            {!scoreSaved && saveError && <p>{saveError}</p>}
            {!scoreSaved && <input onChange={handleChange} placeholder = "username" type = "text"></input>}
            {!scoreSaved && <button onClick={postScore}>Add to leaderboard</button>}
            <br></br>
            <button onClick={handlePlayAgain}>Play Again</button>
        </div> 
        </>
      )
  }

}
