import { useRef, useEffect } from "react"
import "./HangDude.css" 

export default function HangDude(props) {

    const canvasRef = useRef(null)

    const drawHead = (ctx) => {

        let arcPosition = 0;

        const drawCircle = setInterval(() => {
            ctx.beginPath()
            ctx.arc(100, 50, 20, 0, arcPosition * Math.PI)
            ctx.stroke()

            arcPosition += .1

            if(arcPosition > 2.0){
                clearInterval(drawCircle)
            }
        }, 200)


      }

      useEffect(() => {

        const canvas = canvasRef.current
        const context = canvas.getContext('2d')

        //Our draw come here
        drawHead(context)
      }, [drawHead])
    return(
        <div className = "hangDude">
            <canvas ref={canvasRef} {...props} width={200} height={200}/>
        </div>
    )
}
