import "./HangDude.css" 

export default function HangDude() {

    const draw = ctx => {
        ctx.fillStyle = '#000000'
        ctx.beginPath()
        ctx.arc(50, 100, 20, 0, 2*Math.PI)
        ctx.fill()
      }

      useEffect(() => {

        const canvas = canvasRef.current
        const context = canvas.getContext('2d')

        //Our draw come here
        draw(context)
      }, [draw])
    return(
        <div className = "hangDude">
            <canvas ref={canvasRef} {...props}/>
        </div>
    )
}

