import './Board.css';
import { useEffect, useState } from "react"
import Key from "./Key.js"

const row1 = "qwertyuiop";
const row2 = 'asdfghjkl';
const row3 = 'zxcvbnm';


export default function Board (){

    const [correctGuesses, setCorrectGuesses] = useState([])
    const [letterVisibility, setLetterVisibilty] = useState("")
    const [letterList, setletterList] = useState ([])


    const word= "banana";


    // ***** This updates depending on correctGuesses changing
    useEffect(()=> {
        let tempList = [];
        [...word].forEach((char) => {
            tempList.push(
                <div className = "letterDiv">
                    {/* <p className="letter">{}</p> showLetter this index ? show} */}
                    {<p className="letter">{correctGuesses.includes(char) && char}</p>}
                    <p className="underline">_____</p>
                </div>
            )
        })
        setletterList(tempList)    
    }, [correctGuesses])



    // const showCorrectGuess = () => {

    // }   

    var keyRow1 = [];
    var keyRow2 = [];
    var keyRow3 = [];


    const makeKeyBoardRow = (row, keyRow) => {
        [...row].forEach((char) => {
            keyRow.push(<Key word={word}correctGuesses={correctGuesses} setCorrectGuesses={setCorrectGuesses} key={char} char={char}/>)
        })
    }

    makeKeyBoardRow(row1, keyRow1)
    makeKeyBoardRow(row2, keyRow2)
    makeKeyBoardRow(row3, keyRow3)

    return(
        <>
        <div className = "board">
            <div className = "word">
                {letterList}
            </div>
        </div>
         <div className = "keyboard">
            <div className = "keyRow">
                {keyRow1}
            </div>
            <div className = "keyRow">
                {keyRow2}
            </div>
            <div className = "keyRow">
                {keyRow3}
            </div>
        </div>
        </>
    )
}




