import { useRef, useEffect } from "react"
import "./WordMan.css" 

export default function WordMan(props) {

    const canvasRef = useRef(null)

    const drawHead = (ctx) => {
        let arcPosition = 0;
        const drawCircle = setInterval(() => {
            ctx.beginPath()
            ctx.arc(100, 50, 20, 0, arcPosition * Math.PI)
            ctx.stroke()
            arcPosition += .05
            if(arcPosition > 2.1){
                clearInterval(drawCircle)
            }
        }, 50)
      }

      const drawTorso = (ctx) => {
          let lineY = 70;
          const drawVerticalLine = setInterval(() => {
            ctx.beginPath()
            ctx.moveTo(100, lineY)
            ctx.lineTo(100, lineY + 10)
            ctx.stroke()
            lineY += 10;
            if(lineY > 130){
                clearInterval(drawVerticalLine)
            }
          }, 50)
      }

      const drawArm = (ctx, direction) => {
          let lineY = 90;
          let lineX = 100;
          const drawArmLine = setInterval(() => {
            ctx.beginPath()
            ctx.moveTo(lineX, lineY)
            if(direction === "left"){
                ctx.lineTo(lineX - 10, lineY - 10)
                ctx.stroke()
                lineX -= 10
                lineY -= 10
            } else {
                ctx.lineTo(lineX + 10, lineY - 10)
                ctx.stroke()
                lineX += 10
                lineY -= 10
            }

            if (lineX <= 50 || lineX >= 150){
                clearInterval(drawArmLine)
            }

          }, 100)
      }

      const drawLeg = (ctx, direction) => { 
        let lineY = 140;
        let lineX = 100;
        const drawLegLine = setInterval(() => {
          ctx.beginPath()
          ctx.moveTo(lineX, lineY)
          if(direction === "left"){
              ctx.lineTo(lineX - 10, lineY + 10)
              ctx.stroke()
              lineX -= 10
              lineY += 10
          } else {
              ctx.lineTo(lineX + 10, lineY + 10)
              ctx.stroke()
              lineX += 10
              lineY += 10
          }
          if (lineX <= 50 || lineX >= 150){
              clearInterval(drawLegLine)
          }
        }, 50)
      }

      const drawEyes = (ctx, xPosition) => {
        let arcPosition = 0;
        const drawEye = setInterval(() => {
            ctx.beginPath()
            ctx.arc(xPosition, 45, 4, 0, arcPosition * Math.PI)
            ctx.stroke()
            arcPosition += .05
            if(arcPosition > 2.1){
            clearInterval(drawEye)
            }
        }, 30)
      }

      const drawMouth = (ctx) => {
        let arcPosition = 0;
        const draw = setInterval(() => {
            ctx.beginPath()
            ctx.arc(100, 53, 8, 0, arcPosition * Math.PI)
            ctx.stroke()
            arcPosition += .05
            if(arcPosition > 1.0){
            clearInterval(draw)
            }
        }, 30)
      }
      const canvas = canvasRef.current
      const context = canvas.getContext('2d')
      context.lineWidth = 2;
      context.strokeStyle = "#FF0000"
      drawHead(context)
      drawTorso(context)
      drawArm(context, "left")
      drawArm(context, "right")
      drawLeg(context, "left")
      drawLeg(context, "right")
      drawEyes(context, 107, 93)
      drawMouth(context)
      drawHead(context)

      useEffect(() => {

        const canvas = canvasRef.current
        const context = canvas.getContext('2d')
        context.lineWidth = 2;
        context.strokeStyle = "#FFFFFF"



        switch(props.wrongGuesses){
            case 0:
                context.clearRect(0, 0, canvas.width, canvas.height);
                break;
            case 1:
                drawHead(context)
                break;
            case 2:
                drawTorso(context)
                break;
            case 3: 
                drawArm(context, "left")  
                break;
            case 4:
                drawArm(context, "right")
                break;
            case 5: 
                drawLeg(context, "left")
                break;
            case 6:
                drawLeg(context, "right")
                break;
            case 7:
                drawEyes(context, 107)
                break;
            case 8: 
                drawEyes(context, 93)
                break;
            case 9:
                drawMouth(context)
                break;    
        }
      }, [props.wrongGuesses])
    return(
        <div className = "hangDude">
            <canvas ref={canvasRef}  width={200} height={200}/> {/*...props*/}
        </div>
    )
}
